import React from "react";
import ReactDOM from "react-dom";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: "" };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.getResults = this.getResults.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    alert("A list was submitted: " + this.state.value);
    event.preventDefault();
  }

  getResults() {
    this.setState({ value: this.state.value.trim() });
    console.log(this.state.value);
    let regex = /\s+/;
    let lst = this.state.value
      .split(regex)
      .filter((val) => val != " ")
      .map((val) => Number(val));
    
    lst.sort((a, b) => a-b);
    console.log(lst);
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <div id="inp">
          <label htmlFor="list">Enter an Array:</label>
          <p>
            Input example: <strong>-1 0 1 2 -1 -4</strong>
          </p>
        </div>
        <div id="inp2">
          <input
            name="list"
            type="text"
            value={this.state.value}
            onChange={this.handleChange}
          />
          <input
            type="button"
            value="Submit"
            onClick={(this.handleSubmit, this.getResults)}
          />
        </div>
      </form>
    );
  }
}

ReactDOM.render(<App />, document.getElementById("result"));
